

$(function() {
 
    autocompletarCEP("#AssociadoCep", 
    "#AssociadoEndereco", 
    "#AssociadoBairro", 
    "#AssociadoCidade", 
    "#AssociadoEstado");
});


function autocompletarCEP(i, d, g, c, j) {
    var e = $(i);
    var endereco = $(d);
    var bairro = $(g);
    var cidade = $(c);
    var estado = $(j);
    e.blur(function() {
        if ($.trim(e.val()) !== "") {
            $.jsonp({url: "http://correiosapi.apphb.com/cep/" + e.val().replace("-", ""), callbackParameter: "callback", success: function(k) {
                    endereco.val(unescape(k.tipoDeLogradouro) + ": " + unescape(k.logradouro));
                    bairro.val(unescape(k.bairro));
                    cidade.val(unescape(k.cidade));
                    estado.val(unescape(k.estado));
                }, error: function(l) {
                    var k = "";
                    $("#error").html('<div class="alert alert-danger alert-fixed fade in custom-danger container"><button aria-hidden="true" data-dismiss="alert" class="close" type="button"><i class="glyphicon glyphicon-remove"></i></button>\n\nCEP: N&atilde;o encontrado este endereco!</div>');
                }});
        }
    });
}
;